import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {Link} from 'react-router-dom';
import GoogleLogin from 'react-google-login';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

import Login from './pages/Login'
import Partners from './pages/Partners'
import Staff from './pages/Staff';
import Students from './pages/Students';

import socket from './components/SocketUser';

//Join is login stuff

var profile;                     // google user profile
var userID; 

class App extends Component{
  constructor(props){
    super(props);

    this.initialState = {loggedIn: false};
    this.state = this.initialState;

    // const socket = this.socket;
    // console.log("LOOK HERE: " + socket);
    socket.emit('FINALLY', {msg: "event from App.js"});

  }


  onSignIn = (googleUser) => {
    this.setState({loggedIn: true});
    console.log("signed in!");

    profile = googleUser.getBasicProfile();
    userID = profile.getId();

    console.log('ID: ' + profile.getId()); // Do not send to your backend! Use an ID token instead.
    console.log('Name: ' + profile.getName());
    console.log('Given Name: ' + profile.getGivenName());
    console.log('Email: ' + profile.getEmail()); // This is null if the 'email' scope is not present.

    console.log("Entered console.... "  + this.loggedIn);

  }

  onSignOut = () => {
    this.setState({loggedIn: false});

    var auth2 = window.gapi.auth2.getAuthInstance();
    auth2.signOut().then(function () {
      console.log('User signed out.');
    });
  }


  render(){
    return(
      <Router>
        <Switch>

          <Route exact path="/" >
            <Login/>
          </Route>

          <Route exact path="/Login" onSignIn = {this.onSignIn} onSignOut = {this.onSignOut} >
            <Login/>
          </Route>

          <Route exact path="/staff" >
            <Staff/>
          </Route>

          <Route exact path="/students" >
            <Students/>
          </Route>

          <Route exact path="/partners">
            <Partners/>
          </Route>

        </Switch>
    </Router>
    )
  }

}



export default App;
