import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {Link} from 'react-router-dom';
import socket from './SocketUser';
import GoogleLogin from 'react-google-login';


var profile;                     // google user profile
var userID; 

class Login extends Component {

  constructor(props){
    super(props);
    // this.state = ;
    //you can define initialState with a var, let, or const; OR you can just use this. notation throughout
    this.initialState = {loggedIn: false};
    this.state = this.initialState;
  }

    responseGoogle = (response) => {
      console.log("FAILED TO SIGN IN. " + JSON.stringify(response));
    }

  onSignIn = (googleUser) => {
    this.setState({loggedIn: true});
    console.log("signed in!");

    profile = googleUser.getBasicProfile();
    userID = profile.getId();

    console.log('ID: ' + profile.getId()); // Do not send to your backend! Use an ID token instead.
    console.log('Name: ' + profile.getName());
    console.log('Given Name: ' + profile.getGivenName());
    console.log('Email: ' + profile.getEmail()); // This is null if the 'email' scope is not present.

    console.log("Entered console.... "  + this.loggedIn);

  }

  signOut = () => {
    this.setState({loggedIn: false});

    var auth2 = window.gapi.auth2.getAuthInstance();
    auth2.signOut().then(function () {
      console.log('User signed out.');
    });
  }


  render() {
    // const isLoggedIn = this.loggedIn;
    // if (isLoggedIn == false){
    if(this.state.loggedIn == false){
    return(
      <div>
        <h1> TEST </h1>
        <h2> create here </h2>
        <GoogleLogin
          clientId="273539098251-5nhctai82l0ram9s38gkp7s22ahc4lui.apps.googleusercontent.com"
          buttonText="Login"
          onSuccess={this.onSignIn}
          onFailure={this.responseGoogle}
          cookiePolicy={'single_host_origin'}
        />
      </div>
      )}
    else{
      return (
        <div>
          <h1> Logged in! </h1>
          <h2> create here </h2>
          <div></div>
          <button id = "SignOut" onClick = {this.signOut}> Sign Out Here </button> 
        </div>
      )
    }

  }
}





