import React, { Component } from 'react';
import GoogleLogin from 'react-google-login';

import { Button, Form, Col, FormGroup, Checkbox } from 'react-bootstrap'

import socket from '../../../components/SocketUser';
import { Column, Row } from 'simple-flexbox';


class CreateStep2 extends Component {

  constructor(props){
    super(props);
    this.state = {partnerShared: null, partnerURL: null, studentShared: null, studentURL: null}
  }

  createWorkspaceHandler = () => {
    let partners = document.getElementById('formPartnersSheet').value;
    let students = document.getElementById('formStudentsSheet').value;


    socket.emit("linkPartnerUrl",
      {sharingPartner: this.state.partnerShared, partnerURL: this.state.partnerURL});
    console.log("Clicked button to progress");


    socket.on("sheetShared", (data)=>{
      console.log("entered sheetShared");

      //need to check if the url hasn't been used before
      if(data.shared == true){
        this.setState({sheetShared: true})
      } 

      if(data.shared == true && this.props.step == "Step1"){
        this.props.toNextStep();
      }

    });


  }


  //need to make the sheetsharing stuff an actual error message
  formSubmit = (event) => {
    event.preventDefault();
    this.createWorkspaceHandler();


  }

  render() {
    if(this.props.step == "Step2"){
      return(

         <div className="center-me">

            <Column horizontal='center' flexGrow={1}>

            <Row>
              <h2> Create a Workspace </h2>
            </Row>

            <Row horizontal='center'>
              <Form onSubmit = {this.formSubmit}>
                <Form.Group controlId="formPartnersSheet">
                  <Form.Label>Partner Google Sheet URL</Form.Label>
                  <Form.Control required type="text" placeholder="Enter URL" />
                  <Form.Text className="text-muted">
                    Enter the URL for your PARTNER Google Sheet
                  </Form.Text>
                </Form.Group>

                <Form.Group controlId="formStudentsSheet">
                  <Form.Label>Student Google Sheet URL</Form.Label>
                  <Form.Control required type="text" placeholder="Enter URL" />
                  <Form.Text className="text-muted">
                    Enter the URL for your STUDENT Google Sheet
                  </Form.Text>
                </Form.Group>

                <Row horizontal="center">
                  <Button variant="primary" type="submit"> Submit</Button>
                </Row>

              </Form>
            </Row>

            </Column>

         </div>
      )
    } else{
      return null;
    }    
  }

}


export default CreateStep2;
